declare module '*?island' {
  const lazyComponent: {
    (): Promise<{ default: React.ComponentType<any> }>;
    moduleName: string;
    moduleId: string;
  };
  export default lazyComponent;
}

declare module '*?client' {
  const lazyExport: {
    (): Promise<{ default: any }>;
    moduleName: string;
    moduleId: string;
  };
  export default lazyExport;
}
