/**
 * This plugin will intercept requests for @hubspot/cms-components and return minimal versions of the
 * cms-components modules. This prevents HubL-only themes that are not in a Project from breaking since
 * the cms-dev-server relies on certain cms-components modules internally. It is safe to return these
 * minimal versions because the cms-dev-server will not use them in a non-Project environment.
 */
export default function cmsComponentsShimPlugin(): {
    name: string;
    resolveId(id: any): string;
    load(id: any): "\n          export const HSEnvironment = {};\n          export const Hublet = {};\n          export const createEmptyServerRenderContext = () => ({});\n          export const buildStaticEnvConfig = () => ({});\n          export const initConfigSingletonFromJSON = () => ({});\n          export default {};\n        " | "\n          export const EnvironmentConfig = {};\n          export const initConfigSingletonFromJSON = () => ({});\n          export default {};\n        " | "\n          export const Field = {};\n          export const traverseFieldsJSON = () => ({});\n          export default {};\n        " | "export default {};";
};
